  ('dark-n-stormy', 'Dark n Stormy', 'A tropical Florida classic featuring Ponce Rum with ginger beer and lime.', 'ponce-de-leon-rum', '/assets/cocktails/rum-dark-n-stormy.webp', '[{"amount":2,"unit":"oz","item":"Ponce Rum"},{"amount":0.5,"unit":"oz","item":"Lime Juice"},{"amount":null,"unit":null,"item":"Ginger Beer"},{"amount":null,"unit":null,"item":"Lime Wedge"}]'::jsonb, '["Fill a highball glass with ice and add Ponce Rum and lime juice.","Top with ginger beer and stir gently.","Garnish with a lime wedge and serve."]'::jsonb, 'Easy', '3 mins', 'Spicy', 'Tropical', 'A spicy flavor profile with warming notes of ginger and pepper.', 'Ideal for beachside gatherings and tropical-themed parties.'),
  ('rum-fountain-youth', 'Rum Fountain of Youth', 'A refreshing Florida cocktail featuring Ponce Rum with tropical fruits and citrus.', 'ponce-de-leon-rum', '/assets/cocktails/rum-fountain-youth.webp', '[{"amount":2,"unit":"oz","item":"Ponce Rum"},{"amount":1,"unit":"oz","item":"Pineapple Juice"},{"amount":0.5,"unit":"oz","item":"Lime Juice"},{"amount":0.5,"unit":"oz","item":"Simple Syrup"}]'::jsonb, '["Fill a shaker with ice and add Ponce Rum, pineapple juice, lime juice, and simple syrup.","Shake vigorously for 10-15 seconds until well-chilled.","Strain into a highball glass over fresh ice and garnish with pineapple and lime."]'::jsonb, 'Easy', '3 mins', 'Refreshing', 'Tropical', 'A refreshing flavor profile with bright citrus notes and a clean finish.', 'Ideal for beachside gatherings and tropical-themed parties.'),
  ('rum-golden-galleon', 'Rum Golden Galleon', 'A rich Florida cocktail featuring Ponce Rum with honey and spices.', 'ponce-de-leon-rum', '/assets/cocktails/rum-golden-galleon.webp', '[{"amount":2,"unit":"oz","item":"Ponce Rum"},{"amount":0.75,"unit":"oz","item":"Honey Syrup"},{"amount":0.5,"unit":"oz","item":"Lemon Juice"},{"amount":null,"unit":"dash","item":"Allspice"}]'::jsonb, '["Fill a shaker with ice and add Ponce Rum, honey syrup, lemon juice, and allspice.","Shake vigorously for 10-15 seconds until well-chilled.","Strain into a rocks glass over fresh ice and garnish with a lemon twist."]'::jsonb, 'Easy', '3 mins', 'Sweet', 'Tropical', 'A smooth, sweet flavor profile with hints of vanilla and caramel.', 'Ideal for beachside gatherings and tropical-themed parties.'),
  ('mojito', 'Mojito', 'A refreshing Florida mojito featuring Ponce Rum with fresh mint and lime.', 'ponce-de-leon-rum', '/assets/cocktails/rum-governors-mojito.webp', '[{"amount":2,"unit":"oz","item":"Ponce Rum"},{"amount":0.75,"unit":"oz","item":"Lime Juice"},{"amount":0.75,"unit":"oz","item":"Simple Syrup"},{"amount":null,"unit":null,"item":"Fresh Mint"}]'::jsonb, '["Muddle mint leaves with lime juice and simple syrup in a highball glass.","Add Ponce Rum and fill with ice, then top with club soda.","Stir gently and garnish with mint sprigs and a lime wedge."]'::jsonb, 'Easy', '3 mins', 'Refreshing', 'Refreshing', 'A refreshing flavor profile with bright citrus notes and a clean finish.', 'Great for outdoor gatherings and warm-weather celebrations.'),
  ('rum-hot-buttered', 'Rum Hot Buttered', 'A warming Florida cocktail featuring Ponce Rum with butter, spices, and hot water.', 'ponce-de-leon-rum', '/assets/cocktails/rum-hot-buttered.webp', '[{"amount":2,"unit":"oz","item":"Ponce Rum"},{"amount":1,"unit":"oz","item":"Butter"},{"amount":0.5,"unit":"oz","item":"Brown Sugar"},{"amount":null,"unit":null,"item":"Hot Water"}]'::jsonb, '["Combine butter, brown sugar, and spices in a mug, then add Ponce Rum.","Fill with hot water and stir until butter is melted.","Garnish with a cinnamon stick and serve hot."]'::jsonb, 'Medium', '5 mins', 'Rich', 'Classic', 'A rich, complex flavor profile with deep notes of oak and spice.', 'Perfect for any classic cocktail occasion, from happy hour to formal gatherings.'),
  ('rum-jungle-bird', 'Rum Jungle Bird', 'A tropical Florida cocktail featuring Ponce Rum with Campari, pineapple, and lime.', 'ponce-de-leon-rum', '/assets/cocktails/rum-jungle-bird.webp', '[{"amount":1.5,"unit":"oz","item":"Ponce Rum"},{"amount":0.75,"unit":"oz","item":"Campari"},{"amount":1.5,"unit":"oz","item":"Pineapple Juice"},{"amount":0.5,"unit":"oz","item":"Lime Juice"}]'::jsonb, '["Fill a shaker with ice and add Ponce Rum, Campari, pineapple juice, and lime juice.","Shake vigorously for 10-15 seconds until well-chilled.","Strain into a rocks glass over fresh ice and garnish with pineapple and a lime wheel."]'::jsonb, 'Medium', '5 mins', 'Bitter', 'Tropical', 'A sophisticated bitter profile with herbal notes and a dry finish.', 'Ideal for beachside gatherings and tropical-themed parties.'),
  ('planters-punch', 'Planters Punch', 'A classic tropical Florida punch featuring Ponce Rum with citrus and grenadine.', 'ponce-de-leon-rum', '/assets/cocktails/rum-planters-punch.webp', '[{"amount":2,"unit":"oz","item":"Ponce Rum"},{"amount":1,"unit":"oz","item":"Orange Juice"},{"amount":1,"unit":"oz","item":"Pineapple Juice"},{"amount":0.5,"unit":"oz","item":"Grenadine"}]'::jsonb, '["Fill a shaker with ice and add Ponce Rum, orange juice, pineapple juice, and grenadine.","Shake vigorously for 10-15 seconds until well-chilled.","Strain into a highball glass over fresh ice and garnish with fruit."]'::jsonb, 'Easy', '3 mins', 'Refreshing', 'Tropical', 'A refreshing flavor profile with bright citrus notes and a clean finish.', 'Ideal for beachside gatherings and tropical-themed parties.'),
  ('rum-runner-royale', 'Rum Runner Royale', 'A tropical Florida classic featuring Ponce Rum with blackberry and banana liqueurs.', 'ponce-de-leon-rum', '/assets/cocktails/rum-runner-royale.webp', '[{"amount":1.5,"unit":"oz","item":"Ponce Rum"},{"amount":0.5,"unit":"oz","item":"Blackberry Liqueur"},{"amount":0.5,"unit":"oz","item":"Banana Liqueur"},{"amount":1,"unit":"oz","item":"Pineapple Juice"}]'::jsonb, '["Fill a shaker with ice and add Ponce Rum, blackberry liqueur, banana liqueur, and pineapple juice.","Shake vigorously for 10-15 seconds until well-chilled.","Strain into a hurricane glass over fresh ice and garnish with fruit."]'::jsonb, 'Medium', '5 mins', 'Sweet', 'Tropical', 'A smooth, sweet flavor profile with hints of vanilla and caramel.', 'Ideal for beachside gatherings and tropical-themed parties.'),
